import { useSelector } from 'react-redux'
import transcend from '../../assets/images/character/transcendence.png'
import { useState } from 'react'
import arrowup from '../../assets/images/common/arrow-up.png'
import General from '../../assets/images/common/img_card_grade.png'
import Advanced from '../../assets/images/common/img_card_grade2.png'
import Rare from '../../assets/images/common/img_card_grade3.png'
import Hero from '../../assets/images/common/img_card_grade4.png'
import Legend from '../../assets/images/common/img_card_grade5.png'
import Lability from '../../assets/images/common/ability/유물등급.png'
import Rability from '../../assets/images/common/ability/전설등급.png'
import Hability from '../../assets/images/common/ability/영웅등급.png'

const colorMapping = {
    '추가 피해': {
        blue: 0.7,
        purple: 1.6,
        orange: 2.6,
    },
    '적에게 주는 피해': {
        blue: 0.55,
        purple: 1.2,
        orange: 2.0,
    },
    '세레나데 신앙 조화 게이지 획득량': {
        blue: 1.6,
        purple: 3.6,
        orange: 6.0,
    },
    낙인력: {
        blue: 2.15,
        purple: 4.8,
        orange: 8.0,
    },
    '최대 생명력': {
        blue: 1300,
        purple: 3250,
        orange: 6500,
    },
    '공격력+': {
        blue: 80,
        purple: 195,
        orange: 390,
    },
    '무기 공격력+': {
        blue: 195,
        purple: 480,
        orange: 960,
    },
    '최대 마나': {
        blue: 6,
        purple: 15,
        orange: 30,
    },
    '상태이상 공격 지속시간': {
        blue: 0.2,
        purple: 0.5,
        orange: 1.0,
    },
    '전투 중 생명력 회복량': {
        blue: 10,
        purple: 25,
        orange: 50,
    },
    '공격력%': {
        blue: 0.4,
        purple: 0.95,
        orange: 1.55,
    },
    '무기 공격력%': {
        blue: 0.8,
        purple: 1.8,
        orange: 3.0,
    },
    '파티원 회복 효과': {
        blue: 0.95,
        purple: 2.1,
        orange: 3.5,
    },
    '파티원 보호막 효과': {
        blue: 0.95,
        purple: 2.1,
        orange: 3.5,
    },
    '치명타 적중률': {
        blue: 0.4,
        purple: 0.95,
        orange: 1.55,
    },
    '치명타 피해': {
        blue: 1.1,
        purple: 2.4,
        orange: 4.0,
    },
    '아군 공격력 강화 효과': {
        blue: 1.35,
        purple: 3.0,
        orange: 5.0,
    },
    '아군 피해량 강화 효과': {
