        } else if (
            cooldownIndex !== -1 &&
            sortedGemData[idx].Tooltip.includes('작열')
        ) {
            // '재사용'이 포함된 경우 해당 위치부터 끝까지의 문자열을 추출
            return (
                <>
                    {tooltipText
                        .substring(cooldownIndex)
                        .replace(/\s/gi, '=')
                        .replace(/==.*$/gi, '')
                        .replace(/\=/g, ' ')
                        .replace(/추가 효과.*$/g, '')}
                    <div>
                        {tooltipText
                            .substring(cooldownIndex)
                            .replace(/\s/gi, '=')
                            .replace(/==.*$/gi, '')
                            .replace(/\=/g, ' ')
                            .match(/기본 공격력.*?증가/g, '')}
                    </div>
                </>
            )
        } else if (
            (adenIndex1 !== -1 || adenIndex2 !== -1 || adenIndex3 !== -1) &&
            sortedGemData[idx].Tooltip.includes('겁화')
        ) {
            return (
                <>
                    {tooltipText
                        .replace(/\s/gi, ' ')
                        .substring(
                            tooltipText.replace(/\s/gi, '=').indexOf('[')
                        )
                        .match(/지원\s*효과\s*\d+\.\d+\s*%?\s*증가/g)}
                    <div>
                        {tooltipText
                            .replace(/\s/gi, ' ')
                            .substring(
