                    .map(
                        (text) =>
                            text
                                .replace(/<[^A-Za-z]*[A-Za-z][^>]*>/g, '') // 나머지 HTML 제거
                                .replace(/[{}"]/g, '') // { } " 제거
                                .trim() // 공백 제거
                                .replace(/^,/, '') // 맨 앞 , 제거
                                .replace(/,$/, '') // 맨 뒤 , 제거
