    function gemLevel(idx) {
        if (!sortedGemData[idx]) return ''

        let tooltipText = sortedGemData[idx].Tooltip.replace(/<[^>]+>/g, '') // HTML 태그 제거
            .replace(/Element_[0-9]+/g, '')
            .replace(/\r?\n/g, ' ') // 줄바꿈 제거 대신 공백
            .trim()

        let jsxContent = []

        // 피해 증가
        const damageText = tooltipText.match(/피해\s*\d+(\.\d+)?%?\s*증가/g)
        if (damageText) jsxContent.push(...damageText)

        // 재사용 대기시간 감소
        const cooldownText = tooltipText.match(
            /재사용 대기시간\s*\d+(\.\d+)?%/g
        )
        if (cooldownText) jsxContent.push(...cooldownText)

        // 기본 공격력 증가
        const basicAtkText = tooltipText.match(
            /기본 공격력\s*\d+(\.\d+)?%?\s*증가/g
        )
        if (basicAtkText) jsxContent.push(...basicAtkText)

        return (
            <>
                {jsxContent.map((text, i) => (
                    <div key={i}>{text}</div>
                ))}
            </>
        )
    }

