                                    {item.AttackGemIcon && item.CoolGemIcon ? (
                                        <>
                                            <img
                                                style={{
                                                    backgroundImage:
                                                        item.Grade === '유물'
                                                            ? 'linear-gradient(135deg, #341a09, #a24006)'
                                                            : item.Grade ===
                                                              '고대'
                                                            ? 'linear-gradient(135deg, #3d3325, #dcc999)'
                                                            : 'linear-gradient(#3c2201,#a86200)',
                                                }}
                                                src={item.AttackGemIcon}
                                            />
                                            <img
                                                style={{
                                                    backgroundImage:
                                                        item.Grade === '유물'
                                                            ? 'linear-gradient(135deg, #341a09, #a24006)'
                                                            : item.Grade ===
                                                              '고대'
                                                            ? 'linear-gradient(135deg, #3d3325, #dcc999)'
                                                            : 'linear-gradient(#3c2201,#a86200)',
                                                }}
                                                src={item.CoolGemIcon}
                                            />
                                        </>
                                    ) : item.AttackGemIcon &&
                                      !item.CoolGemIcon ? (
                                        <>
                                            <img
                                                style={{
                                                    backgroundImage:
                                                        item.Grade === '유물'
                                                            ? 'linear-gradient(135deg, #341a09, #a24006)'
                                                            : item.Grade ===
                                                              '고대'
                                                            ? 'linear-gradient(135deg, #3d3325, #dcc999)'
                                                            : 'linear-gradient(#3c2201,#a86200)',
                                                }}
                                                src={item.AttackGemIcon}
                                            />
                                            <div className="empty"></div>
                                        </>
                                    ) : !item.AttackGemIcon &&
                                      item.CoolGemIcon ? (
                                        <>
                                            <div className="empty"></div>
                                            <img
                                                style={{
                                                    backgroundImage:
                                                        item.Grade === '유물'
                                                            ? 'linear-gradient(135deg, #341a09, #a24006)'
                                                            : item.Grade ===
                                                              '고대'
                                                            ? 'linear-gradient(135deg, #3d3325, #dcc999)'
                                                            : 'linear-gradient(#3c2201,#a86200)',
                                                }}
                                                src={item.CoolGemIcon}
                                            />
                                        </>
                                    ) : (
                                        <>
                                            <div className="empty"></div>
                                            <div className="empty"></div>
                                        </>
                                    )}
                                </div>
                                <div className="skill-sub-subinfo-rune">
                                    {item.Rune !== null ? (
                                        <>
                                            <img
                                                style={{
                                                    background:
